mapper.select:
select id, strategyId, awardId, awardCount, awardRate, createTime, updateTime
from strategy_detail
where strategyId = #{strategyId};

select awardId
from strategy_detail
where strategyId = #{strategyId}
  AND awardSurplusCount = 0;

mapper.update:
update strategy_detail
set awardSurplusCount = awardSurplusCount - 1
where strategyId = #{strategyId}
  AND awardId = #{awardId}
  AND awardSurplusCount > 0;
